macro.command:
// Draw from a table that contains items and add the items to a selected token's actor.

const table = await fromUuid("Compendium.world.rollable-tables.RollTable.Rw2eHqCJ85FMTOEm");


// Draw items from compendium or sidebar.
const receiver = token?.actor;
if (!table || !receiver) {
  ui.notifications.warn("Missing table or selected token.");
  return null;
}
const draw = await table.draw();

// Filter out results that are not items
const validResults = draw.results.filter(i => i.documentCollection && i.documentId);
const promises = validResults.map(i => {
  const key = i.documentCollection;
  const id = i.documentId;
  const uuid = `Compendium.${key}.Item.${id}`;
  return fromUuid(uuid);
});

const items = (await Promise.all(promises)).filter(item => item); // Remove any null/undefined values
const itemData = items.map(item => {
  let data = item.toObject();
  data.system.attuned = false; // Ensure item is not attuned by default
  return data;
});

if (itemData.length === 0) {
  ui.notifications.warn("No valid items found in the table draw.");
  return;
}

await receiver.createEmbeddedDocuments("Item", itemData);

